# Cherchons les sous-agents dans la session actuelle en analysant le JSONL
grep -E '"type":"skill_progress"' ~/.claude/projects/-home-mks-projects-leann-fork/6a8baa5d-20ca-436c-b371-72a2ec0505b8.jsonl | python3 -c "
import sys, json
sessions = set()
for line in sys.stdin:
    try:
        d = json.loads(line)
        if 'sessionId' in d.get('data', {}).get('message', {}):
            sessions.add(d['data']['message']['sessionId'])
    except: pass
for s in sessions:
    print(s)
" 2>/dev/null
